$(function() {
    let DataUrl = 'https://joes-1251595441.cos.ap-chengdu.myqcloud.com/ha3.json';
    $.get(DataUrl, (res) => {
        let resData = res.data;
        // console.log(resData);
        if (resData.length) {
            $('.loading').css('display', 'none');
            resData.map((item) => {
                $('.v' + item.id).html(item.title);
                jwplayer('v' + item.id).setup({
                    'flashplayer': 'js/jwplayer.flash.swf',
                    'width': '100%',
                    'volume': '100',
                    'aspectratio': '10:6',
                    'image': item.image,
                    'file': item.url
                });
            });
        }
    });
});